mapper.select:
select * 
from user 
where user_email=#{user_email} and is_used = 1;

select count(*)
from user
where user_email=#{user_email} and is_used = 1;

mapper.update:
update user set
user_authKey = #{user_authKey}
where user_email = #{user_email} and is_used = 1;

update user set
user_auth = 1
where user_email = #{email} is_used = 1;

update user set
user_password = #{pw}
where user_email = #{email} and is_used = 1;

mapper.select:
select *
from user
where user_id=#{user_id} and is_used = 1;

mapper.update:
update user
set user_image = #{user_image}, user_nickname=#{user_nickname}, user_location=#{user_location}, user_generation=#{user_generation}
where user_email = #{user_email} and is_used = 1;

update user 
set is_used = 0
where user_id = #{user_id} and is_used = 1;

mapper.select:
