mapper.update:
update subscription
set favorite_flag=IF(favorite_flag=1, 0, 1)
where user_id = #{user_id} and board_id = #{board_id} and is_used = 1;

update subscription
set is_used = 0
where user_id = #{user_id} and board_id = #{board_id} and is_used = 1;

mapper.select:
select count(*)
from quiz
where quiz_answer like #{answer}
